import React from 'react';
import { Pdf, View } from 'bappo-components';
import { Code } from '../../../../ui-explorer';

const httpUrl =
  'https://mozilla.github.io/pdf.js/web/compressed.tracemonkey-pldi-09.pdf';
const definition = {
  content: [
    'Document created from definition',
    {
      layout: 'exampleLayout',
      table: {
        headerRows: 1,
        widths: ['*', 'auto', 100, '*'],

        body: [
          ['First', 'Second', 'Third', 'The last one'],
          ['Value 1', 'Value 2', 'Value 3', 'Value 4'],
          [
            {
              text: 'Bold value',
              bold: true,
              style: {
                font: 'Helvetica',
              },
            },
            'Val 2',
            'Val 3',
            'Val 4',
          ],
        ],
      },
    },
  ],
  info: {
    title: 'Document created from definition',
  },
  defaultStyle: {
    font: 'Open Sans',
  },
};
const tableLayouts = {
  exampleLayout: {
    hLineWidth: (i, node) => {
      if (i === 0 || i === node.table.body.length) {
        return 0;
      }
      return i === node.table.headerRows ? 2 : 1;
    },
    vLineWidth: i => {
      return 0;
    },
    hLineColor: i => {
      return i === 1 ? 'black' : '#aaa';
    },
    paddingLeft: i => {
      return i === 0 ? 0 : 8;
    },
    paddingRight: (i, node) => {
      return i === node.table.widths.length - 1 ? 0 : 8;
    },
  },
};
const fonts = {
  'Open Sans': {
    normal:
      'https://unpkg.com/npm-font-open-sans@1.1.0/fonts/Regular/OpenSans-Regular.ttf',
  },
};

const PdfPropSourceExample = () => {
  return (
    <View>
      <Code>{`source={{ uri: 'https://mozilla.github.io/pdf.js/web/compressed.tracemonkey-pldi-09.pdf' }}`}</Code>
      <Pdf
        source={{
          uri: httpUrl,
        }}
        style={styles.pdf}
      />
      <Code>{`source={{ uri: 'data:application/pdf;base64,xxx' }}`}</Code>
      <Pdf
        source={{
          uri:
            'data:application/pdf;base64,',
        }}
        style={styles.pdf}
      />
      <Code>
        {`source={
  definition: {
    content: [
      'Document created from definition',
      {
        layout: "exampleLayout",
        table: {
          headerRows: 1,
          widths: ['*', 'auto', 100, '*'],
  
          body: [
            ['First', 'Second', 'Third', 'The last one'],
            ['Value 1', 'Value 2', 'Value 3', 'Value 4'],
            [
              {
                text: 'Bold value',
                bold: true,
                style: {
                  font: 'Helvetica',
                },
              },
              'Val 2',
              'Val 3',
              'Val 4',
            ],
          ],
        },
      },
    ],
    info: {
      title: 'Document created from definition'
    },
    defaultStyle: {
      font: "Open Sans"
    },
  },
  tableLayouts: {
    exampleLayout: {
      hLineWidth: (i, node) => {
        if (i === 0 || i === node.table.body.length) {
          return 0;
        }
        return i === node.table.headerRows ? 2 : 1;
      },
      vLineWidth: i => {
        return 0;
      },
      hLineColor: i => {
        return i === 1 ? 'black' : '#aaa';
      },
      paddingLeft: i => {
        return i === 0 ? 0 : 8;
      },
      paddingRight: (i, node) => {
        return i === node.table.widths.length - 1 ? 0 : 8;
      },
    },
  },
  fonts: {
    "Open Sans": {
      normal: "https://unpkg.com/npm-font-open-sans@1.1.0/fonts/Regular/OpenSans-Regular.ttf"
    },
  },
}`}
      </Code>
      <Pdf
        source={{
          definition,
          tableLayouts,
          fonts,
        }}
        style={styles.pdf}
      />
    </View>
  );
};

export default PdfPropSourceExample;

const styles = {
  pdf: {
    height: 480,
    margin: 8,
  },
};
